import React, { useState } from 'react';
import { bankingAPI } from '../services/api';
import './PaymentForm.css'; // We'll create this next

const PaymentForm = () => {
  // State to store form data
  const [payment, setPayment] = useState({
    amount: '',
    currency: 'USD',
    fromAccount: '',
    toAccount: ''
  });
  
  // State for loading and results
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page refresh
    setLoading(true);
    setError('');
    setResult(null);

    try {
      console.log('Sending payment:', payment);
      
      // Send to Mule - this triggers your entire workflow!
      const response = await bankingAPI.processPayment(payment);
      
      console.log('Payment successful:', response.data);
      setResult(response.data);
      
      // Reset form after successful payment
      setPayment({
        amount: '',
        currency: 'USD',
        fromAccount: '',
        toAccount: ''
      });
      
    } catch (err) {
      console.error('Payment failed:', err);
      setError(err.response?.data?.message || 'Payment failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setPayment(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="payment-container">
      <h2>üí≥ Make a Payment</h2>
      
      <form onSubmit={handleSubmit} className="payment-form">
        <div className="form-group">
          <label htmlFor="amount">Amount:</label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={payment.amount}
            onChange={handleChange}
            placeholder="Enter amount"
            required
            min="1"
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="currency">Currency:</label>
          <select 
            id="currency"
            name="currency" 
            value={payment.currency} 
            onChange={handleChange}
          >
            <option value="USD">USD ($)</option>
            <option value="EUR">EUR (‚Ç¨)</option>
            <option value="GBP">GBP (¬£)</option>
            <option value="TND">TND (ÿØ.ÿ™)</option>
            <option value="CAD">CAD (C$)</option>
          </select>
        </div>
        
        <div className="form-group">
          <label htmlFor="fromAccount">From Account:</label>
          <input
            type="text"
            id="fromAccount"
            name="fromAccount"
            value={payment.fromAccount}
            onChange={handleChange}
            placeholder="e.g., ACC12345"
            required
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="toAccount">To Account:</label>
          <input
            type="text"
            id="toAccount"
            name="toAccount"
            value={payment.toAccount}
            onChange={handleChange}
            placeholder="e.g., ACC67890"
            required
          />
        </div>
        
        <button 
          type="submit" 
          className="submit-btn"
          disabled={loading}
        >
          {loading ? '‚è≥ Processing...' : 'üöÄ Send Payment'}
        </button>
      </form>

      {/* Show error message */}
      {error && (
        <div className="error-message">
          ‚ùå {error}
        </div>
      )}

      {/* Show success result */}
      {result && (
        <div className="success-result">
          <h3>‚úÖ Payment Successful!</h3>
          <div className="result-details">
            <p><strong>Transaction ID:</strong> {result.transactionId}</p>
            <p><strong>Status:</strong> {result.status}</p>
            <p><strong>Message:</strong> {result.message}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default PaymentForm;
