import axios from 'axios';

// Remove the MULE_BASE_URL - use relative paths
export const bankingAPI = {
  processPayment: async (paymentData) => {
    console.log('üöÄ Sending payment to Mule via proxy...');
    
    try {
      const response = await fetch('/payments', {  // ‚Üê Remove http://localhost:8090
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(paymentData)
      });
      
      // ... rest of your code stays the same
    } catch (error) {
      console.error('‚ùå Payment failed:', error);
      throw error;
    }
  },

  // Get transactions - also use relative path
  getTransactions: () => {
    return axios.get('/transactions');  // ‚Üê Remove http://localhost:8082
  }
};
