import axios from 'axios';

// Base URL for your Mule application
const MULE_BASE_URL = 'http://localhost:8090';

// Create axios instance for Mule with better configuration
const muleAPI = axios.create({
  baseURL: MULE_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 30000,
  transformRequest: [
    (data, headers) => {
      console.log('ðŸ” Axios sending data:', data);
      console.log('ðŸ” Axios headers:', headers);
      return JSON.stringify(data);
    }
  ]
});

// Your Spring Boot services
const SPRING_BOOT_SERVICES = {
  transaction: 'http://localhost:8082',
  fraud: 'http://localhost:8083', 
  notification: 'http://localhost:8084'
};

// API functions
export const bankingAPI = {
  // Send payment to Mule (this triggers your entire workflow)
  processPayment: async (paymentData) => {
    console.log('ðŸš€ Sending payment to Mule:', paymentData);
    
    try {
      // Test with fetch first to see what's happening
      const testResponse = await fetch('http://localhost:8090/payments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(paymentData)
      });
      
      console.log('ðŸ“¡ Fetch response status:', testResponse.status);
      console.log('ðŸ“¡ Fetch response headers:', testResponse.headers);
      
      if (!testResponse.ok) {
        const errorText = await testResponse.text();
        console.error('âŒ Fetch error response:', errorText);
        throw new Error(`HTTP error! status: ${testResponse.status}`);
      }
      
      const result = await testResponse.json();
      console.log('âœ… Fetch success:', result);
      return { data: result };
      
    } catch (error) {
      console.error('âŒ Payment failed:', error);
      throw error;
    }
  },

  // Get transactions from your Spring Boot transaction service
  getTransactions: () => {
    return axios.get(`${SPRING_BOOT_SERVICES.transaction}/transactions`);
  },

  // Get fraud analysis
  getFraudCheck: (transactionId) => {
    return axios.get(`${SPRING_BOOT_SERVICES.fraud}/fraud-check/${transactionId}`);
  }
};

export default bankingAPI;
