import axios from 'axios';

const MULE_BASE_URL = 'http://localhost:8090';

export const bankingAPI = {
  // Send payment to Mule - SIMPLE VERSION
  processPayment: async (paymentData) => {
    console.log('ðŸš€ Sending payment to Mule:', paymentData);
    
    try {
      const response = await fetch(`${MULE_BASE_URL}/payments`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(paymentData)
      });
      
      console.log('ðŸ“¡ Response status:', response.status);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const result = await response.json();
      console.log('âœ… Success:', result);
      return { data: result };
      
    } catch (error) {
      console.error('âŒ Payment failed:', error);
      throw error;
    }
  },

  // Get transactions
  getTransactions: () => {
    return axios.get('http://localhost:8082/transactions');
  }
};

export default bankingAPI;
