import React, { useState } from 'react';
import { bankingAPI } from '../services/api';
import './PaymentForm.css';

const PaymentForm = () => {
  // State to store form data
  const [payment, setPayment] = useState({
    amount: '',
    currency: 'USD',
    fromAccount: '',
    toAccount: ''
  });
  
  // State for loading and results
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page refresh
    setLoading(true);
    setError('');
    setResult(null);

    try {
      console.log('üöÄ Sending payment to Mule:', payment);
      
      // Validate form data
      if (!payment.amount || !payment.fromAccount || !payment.toAccount) {
        throw new Error('Please fill in all required fields');
      }

      if (parseFloat(payment.amount) <= 0) {
        throw new Error('Amount must be greater than 0');
      }

      // Send to Mule - this triggers your entire workflow!
      const response = await bankingAPI.processPayment(payment);
      
      console.log('‚úÖ Payment successful - Full response:', response);
      console.log('‚úÖ Payment response data:', response.data);
      
      setResult(response.data);
      
      // Reset form after successful payment
      setPayment({
        amount: '',
        currency: 'USD',
        fromAccount: '',
        toAccount: ''
      });
      
      // Refresh transactions after successful payment
      setTimeout(() => {
        // This will refresh the transaction history if we're on that tab
        window.dispatchEvent(new Event('transactionUpdated'));
      }, 1000);
      
    } catch (err) {
      console.error('‚ùå Payment failed - Full error object:', err);
      console.error('‚ùå Payment failed - Error message:', err.message);
      console.error('‚ùå Payment failed - Error stack:', err.stack);
      
      if (err.response) {
        // Server responded with error status
        console.error('‚ùå Payment failed - Response status:', err.response.status);
        console.error('‚ùå Payment failed - Response headers:', err.response.headers);
        console.error('‚ùå Payment failed - Response data:', err.response.data);
        
        let errorMessage = 'Payment failed. Please try again.';
        
        if (err.response.data) {
          if (err.response.data.message) {
            errorMessage = err.response.data.message;
          } else if (typeof err.response.data === 'string') {
            errorMessage = err.response.data;
          } else if (err.response.data.error) {
            errorMessage = err.response.data.error;
          }
        }
        
        if (err.response.status === 500) {
          errorMessage = 'Server error. Please check if Mule and all Spring Boot services are running.';
        } else if (err.response.status === 404) {
          errorMessage = 'Service not found. Please check if Mule is running on port 8090.';
        } else if (err.response.status === 400) {
          errorMessage = 'Bad request. Please check your payment details.';
        } else if (err.response.status === 403) {
          errorMessage = 'Transaction blocked by fraud detection.';
        }
        
        setError(errorMessage);
      } else if (err.request) {
        // Request was made but no response received
        console.error('‚ùå Payment failed - No response received. Request details:', err.request);
        setError('No response from server. Please check if Mule is running on port 8090 and all services are started.');
      } else {
        // Something else happened
        console.error('‚ùå Payment failed - Other error:', err.message);
        setError(err.message || 'An unexpected error occurred. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setPayment(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="payment-container">
      <h2>üí≥ Make a Payment</h2>
      <p className="payment-instructions">
        This will send a payment through Mule ESB to your Spring Boot microservices
      </p>
      
      <form onSubmit={handleSubmit} className="payment-form">
        <div className="form-group">
          <label htmlFor="amount">
            Amount: <span className="required">*</span>
          </label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={payment.amount}
            onChange={handleChange}
            placeholder="Enter amount"
            required
            min="1"
            step="0.01"
            disabled={loading}
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="currency">
            Currency: <span className="required">*</span>
          </label>
          <select 
            id="currency"
            name="currency" 
            value={payment.currency} 
            onChange={handleChange}
            disabled={loading}
          >
            <option value="USD">USD ($)</option>
            <option value="EUR">EUR (‚Ç¨)</option>
            <option value="GBP">GBP (¬£)</option>
            <option value="TND">TND (ÿØ.ÿ™)</option>
            <option value="CAD">CAD (C$)</option>
          </select>
        </div>
        
        <div className="form-group">
          <label htmlFor="fromAccount">
            From Account: <span className="required">*</span>
          </label>
          <input
            type="text"
            id="fromAccount"
            name="fromAccount"
            value={payment.fromAccount}
            onChange={handleChange}
            placeholder="e.g., ACC12345"
            required
            disabled={loading}
          />
          <small className="input-hint">Avoid using "BLOCKED" in account name to prevent fraud detection</small>
        </div>
        
        <div className="form-group">
          <label htmlFor="toAccount">
            To Account: <span className="required">*</span>
          </label>
          <input
            type="text"
            id="toAccount"
            name="toAccount"
            value={payment.toAccount}
            onChange={handleChange}
            placeholder="e.g., ACC67890"
            required
            disabled={loading}
          />
        </div>
        
        <button 
          type="submit" 
          className="submit-btn"
          disabled={loading}
        >
          {loading ? (
            <>
              <span className="spinner"></span>
              ‚è≥ Processing Payment...
            </>
          ) : (
            'üöÄ Send Payment'
          )}
        </button>
      </form>

      {/* Show error message */}
      {error && (
        <div className="error-message">
          <div className="error-header">
            <span className="error-icon">‚ùå</span>
            <strong>Payment Failed</strong>
          </div>
          <div className="error-details">{error}</div>
          <div className="error-tip">
            üí° Check that all services are running: Mule (8090), Spring Boot (8082-8084), ActiveMQ, MySQL
          </div>
        </div>
      )}

      {/* Show success result */}
      {result && (
        <div className="success-result">
          <div className="success-header">
            <span className="success-icon">‚úÖ</span>
            <strong>Payment Successful!</strong>
          </div>
          <div className="result-details">
            <div className="result-item">
              <span className="result-label">Transaction ID:</span>
              <span className="result-value">{result.transactionId}</span>
            </div>
            <div className="result-item">
              <span className="result-label">Status:</span>
              <span className={`result-value status-${result.status?.toLowerCase()}`}>
                {result.status}
              </span>
            </div>
            <div className="result-item">
              <span className="result-label">Amount:</span>
              <span className="result-value">
                {result.amount} {result.currency}
              </span>
            </div>
            {result.message && (
              <div className="result-item">
                <span className="result-label">Message:</span>
                <span className="result-value">{result.message}</span>
              </div>
            )}
          </div>
          <div className="success-note">
            ‚úÖ Payment processed through Mule ESB workflow
            <br />
            ‚úÖ Check Slack/Discord/Email for notifications
            <br />
            ‚úÖ Transaction saved to database
          </div>
        </div>
      )}

      {/* Debug info */}
      <div className="debug-info">
        <details>
          <summary>üîß Debug Information</summary>
          <div className="debug-content">
            <p><strong>Mule Endpoint:</strong> POST http://localhost:8090/payments</p>
            <p><strong>Current Form Data:</strong></p>
            <pre>{JSON.stringify(payment, null, 2)}</pre>
            <p><strong>Services to check:</strong></p>
            <ul>
              <li>Mule ESB: port 8090</li>
              <li>Transaction Service: port 8082</li>
              <li>Fraud Service: port 8083</li>
              <li>Notification Service: port 8084</li>
              <li>ActiveMQ: port 61616</li>
              <li>MySQL: port 3306</li>
            </ul>
          </div>
        </details>
      </div>
    </div>
  );
};

export default PaymentForm;
