import React, { useState, useEffect } from 'react';
import './CurrencyInfo.css';

const CurrencyInfo = () => {
  const [currencies, setCurrencies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // First, get all countries from the SOAP service
  const getAllCountries = async () => {
    try {
      const response = await fetch('/countries');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      return data.countries || [];
    } catch (err) {
      console.error('Failed to fetch countries:', err);
      // Fallback to common countries if the countries endpoint fails
      return [
        { sISOCode: 'US', sName: 'United States' },
        { sISOCode: 'GB', sName: 'United Kingdom' },
        { sISOCode: 'TN', sName: 'Tunisia' },
        { sISOCode: 'FR', sName: 'France' },
        { sISOCode: 'CA', sName: 'Canada' },
        { sISOCode: 'DE', sName: 'Germany' },
        { sISOCode: 'JP', sName: 'Japan' },
        { sISOCode: 'AU', sName: 'Australia' }
      ];
    }
  };

  const fetchCurrency = async (countryCode, countryName) => {
    try {
      const response = await fetch(`/currency/${countryCode}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      
      // Parse the SOAP response correctly
      const currencyData = data.CountryCurrencyResponse || data;
      
      return {
        countryCode: countryCode,
        countryName: countryName,
        currencyCode: currencyData.sISOCode || currencyData.currency?.code || 'N/A',
        currencyName: currencyData.sName || currencyData.currency?.name || 'N/A',
        service: 'SOAP Service',
        timestamp: new Date().toISOString()
      };
    } catch (err) {
      console.error(`Error fetching currency for ${countryCode}:`, err);
      return {
        countryCode: countryCode,
        countryName: countryName,
        currencyCode: 'Error',
        currencyName: 'Failed to fetch',
        service: 'SOAP Service',
        timestamp: new Date().toISOString(),
        error: err.message
      };
    }
  };

  const fetchAllCurrencies = async () => {
    setLoading(true);
    setError('');
    
    try {
      // First get all countries
      const countries = await getAllCountries();
      console.log('Fetched countries:', countries);
      
      // Then fetch currency for each country (limit to first 20 for performance)
      const limitedCountries = countries.slice(0, 20);
      
      const currencyPromises = limitedCountries.map(country => 
        fetchCurrency(country.sISOCode, country.sName)
      );
      
      const results = await Promise.all(currencyPromises);
      setCurrencies(results);
    } catch (err) {
      setError('Failed to fetch currency information. Please check if Mule is running on port 8090.');
      console.error('Currency fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAllCurrencies();
  }, []);

  return (
    <div className="currency-info">
      <h2>üí± Currency Information</h2>
      <p className="currency-description">
        Real-time currency data fetched from SOAP Web Service
      </p>
      
      <div className="controls">
        <button 
          onClick={fetchAllCurrencies} 
          className="refresh-btn"
          disabled={loading}
        >
          {loading ? 'üîÑ Refreshing...' : 'üîÑ Refresh All Currencies'}
        </button>
        <span className="count-badge">
          Showing {currencies.length} currencies
        </span>
      </div>

      {error && (
        <div className="error-message">
          <div className="error-header">
            <span className="error-icon">‚ùå</span>
            <strong>Service Error</strong>
          </div>
          <div className="error-details">{error}</div>
        </div>
      )}

      {loading && (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Fetching currency data from SOAP service...</p>
        </div>
      )}

      <div className="currencies-grid">
        {currencies.map((currency, index) => (
          <div key={`${currency.countryCode}-${index}`} className={`currency-card ${currency.error ? 'error-card' : ''}`}>
            <div className="country-flag">
              {currency.countryCode}
            </div>
            <div className="currency-details">
              <h3 className="country-name">{currency.countryName}</h3>
              <div className="currency-data">
                <div className="currency-code">
                  <span className="label">Currency Code:</span>
                  <span className="value code">{currency.currencyCode}</span>
                </div>
                <div className="currency-name">
                  <span className="label">Currency Name:</span>
                  <span className="value">{currency.currencyName}</span>
                </div>
              </div>
              <div className="service-info">
                <span className="service-type">{currency.service}</span>
                {currency.timestamp && (
                  <span className="timestamp">
                    {new Date(currency.timestamp).toLocaleTimeString()}
                  </span>
                )}
              </div>
              {currency.error && (
                <div className="card-error">
                  <small>{currency.error}</small>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CurrencyInfo;
